import React from 'react'
import useMovieById from '../hooks/useMovieById'
import { useSelector } from 'react-redux';

export default function VideoBackground({movieId,bool}) {
  console.log(movieId);
  useMovieById(movieId);
  const trailer = useSelector(store=>store.movie.trailerMovie);
  console.log(trailer.key);
  return (
    <div className='w-screen'>
        <iframe
        className={'${bool ? "w-[1005]" :"w-screen aspect-video"} '}
        src={`https://www.youtube.com/embed/${trailer.key}?si=RaQctxiXXab81BnK&autoplay=1&mute=1`}
        // src="https://www.youtube.com/embed/0fwrMYPcGQ0?si=HFyqqciM1l8id3Ud"
         title="YouTube video player" 
         frameborder="0" 
         allowFullScreen> 
         </iframe>
    </div>
  )
}
