import React,{useState} from 'react'
import Header from './Header';
export default function Login() {
  const [isLogin, setIsLogin]= useState(false);
  const [fullName, setFullName]= useState("");
  const [email, setEmail]= useState("");
  const [password, setPassword]= useState("");

  const loginHandler=()=>{
    setIsLogin(!isLogin);
  }
  return (
    <div>
      <Header/>
      <div className='absolute '>
        <img className=' w-[100vw] h-[100vh]' src="https://cdn.mos.cms.futurecdn.net/rDJegQJaCyGaYysj2g5XWY-970-80.jpg.webp" alt="banner" />
      </div>
      <form className='flex flex-col w-3/12 p-12 items-center justify-center absolute my-36 left-0 right-0 mx-auto bg-black opacity-85 rounded-md' >
        <h1 className= ' text-3xl text-white mb-5 font-bold'>{isLogin ? "Login" : "Sign Up"}</h1>
        <div className='flex flex-col'>
          {
            !isLogin && <input type="text"  placeholder='Full Name' className='outline-none p-3 my-2 rounded-sm bg-gray-800 text-white'/>

          }
          <input type="email"   placeholder='Email' className='outline-none p-3 my-2 rounded-sm bg-gray-800 text-white'/>
          <input type="password" placeholder='Password'  className='outline-none p-3 my-2 rounded-sm bg-gray-800 text-white ' />
        <p className=' text-white'>{isLogin ? "New to Netflix?" : "Already have an Account"}<span  onClick={loginHandler} className=' ml-1 text-blue-900 font-medium cursor-pointer'>{isLogin ? "Sign Up" : "Login"}</span></p>
        </div>

      </form>
    </div>
  )
}
