import { createSlice } from "@reduxjs/toolkit";

const searchSlice = createSlice({
    name:"search",
    initialState:{
        movieName:null,
        searchedMovie:null
    },
    reducers:{
        setMovieName:(state,action)=>{
            state.movieName= action.payload;
        }
    }
})
