import { createSlice } from "@reduxjs/toolkit";
import { Upcoming_Movie } from "../utils/constant";

const movieSlice=createSlice({
    name:'movie',
    initialState:{
        nowPlayingMovies:null,
        popularMovies: null,
        topRaitedMovies:null,
        upcomingMovies:null
    },
    reducers:{
        //actions
        getNowPlayingMovies:(state,action)=>{
            state.nowPlayingMovies=action.payload;
        },
        getPopularMovies:(state,action)=>{
            state.popularMovies=action.payload;
        },
        getTopRaitedMovies:(state,action)=>{
            state.topRaitedMovies=action.payload;
        },
        getUpcomingMovies:(state,action)=>{
            state.upcomingMovies=action.payload;
        }
    }
});

export const {getNowPlayingMovies,getPopularMovies,getTopRaitedMovies,getUpcomingMovies} = movieSlice.actions;
export default movieSlice.reducer;
