import {createSclice} from "@reduxjs/toolkit";

const userSlice = createSclice({
    name:"user",
    initialState:{
        user:null
    },
    reducers:{
        setUser:(state,action)=>{
            state.user = action.playload;
        }
    }
})

export const {setUser} = userSlice.actions;
export default userSlice.reducer;
